mapper.select:
select DATEPART(YEAR,xdrq) as year, DATEPART(month,xdrq) as month, sum(case when DATEPART(day,xdrq) between 1 and 7 then sl else 0 end) 'one', sum(case when DATEPART(day,xdrq) between 8 and 14 then sl else 0 end) 'two', sum(case when DATEPART(day,xdrq) between 15 and 21 then sl else 0 end) 'three', sum(case when DATEPART(day,xdrq) between 22 and 30 then sl else 0 end) 'four' from View_buss_order where year(xdrq)=#{year}
and gys=#{supplyId}
group by DATEPART(YEAR,xdrq),DATEPART(month,xdrq)

select DATEPART(YEAR,jhrq) as year, DATEPART(month,jhrq) as month, sum(case when DATEPART(day,jhrq) between 1 and 7 then sl else 0 end) 'one', sum(case when DATEPART(day,jhrq) between 8 and 14 then sl else 0 end) 'two', sum(case when DATEPART(day,jhrq) between 15 and 21 then sl else 0 end) 'three', sum(case when DATEPART(day,jhrq) between 22 and 30 then sl else 0 end) 'four' from View_buss_order where year(jhrq)=#{year}
and gys=#{supplyId}
group by DATEPART(YEAR,jhrq),DATEPART(month,jhrq)

select  (select lastname from hrmresource where id=euserid) as name ,count(id) as total from View_new_model_all
  WHERE  1 = 1

     and  cdate>=#{startDate}


     and  cdate<=#{endDate}

  group by euserid  order by total  desc
